import React from 'react';
import { Box, Button, Typography, Paper } from '@mui/material';
import { GoogleMap, Marker, StreetViewPanorama } from '@react-google-maps/api';
import { Landmark } from '../types';

interface NavigationViewProps {
  landmarks: Landmark[];
  currentIndex: number;
  isComplete: boolean;
  onMarkArrived: () => void;
  onNextLandmark: () => void;
  onPreviousLandmark: () => void;
  onShowOverview: () => void;
}

const NavigationView: React.FC<NavigationViewProps> = ({
  landmarks,
  currentIndex,
  isComplete,
  onMarkArrived,
  onNextLandmark,
  onPreviousLandmark,
  onShowOverview,
}) => {
  const currentLandmark = landmarks[currentIndex];
  const isLoaded = window.google !== undefined;

  if (!isLoaded) {
    return <Typography>Loading Google Maps...</Typography>;
  }

  return (
    <Box>
      <Paper sx={{ p: 2, mb: 3 }}>
        <Typography variant="h5" gutterBottom>
          Navigation Progress
        </Typography>
        <Typography>
          Landmark {currentIndex + 1} of {landmarks.length}: {currentLandmark.name}
        </Typography>
        <Typography variant="body2" color="text.secondary">
          {currentLandmark.address}
        </Typography>
      </Paper>

      <Box sx={{ display: 'flex', gap: 2, mb: 3 }}>
        <Box sx={{ flex: 1, height: 400 }}>
          <GoogleMap
            mapContainerStyle={{ width: '100%', height: '100%' }}
            center={currentLandmark.position}
            zoom={15}
          >
            {landmarks.map((landmark) => (
              <Marker
                key={landmark.id}
                position={landmark.position}
                icon={{
                  url: landmark.id === currentLandmark.id 
                    ? 'https://maps.google.com/mapfiles/ms/icons/red-dot.png'
                    : 'https://maps.google.com/mapfiles/ms/icons/blue-dot.png',
                  scaledSize: new google.maps.Size(32, 32),
                }}
              />
            ))}
          </GoogleMap>
        </Box>
        <Box sx={{ flex: 1, height: 400 }}>
          <StreetViewPanorama
            position={currentLandmark.position}
            options={{
              visible: true,
              addressControl: false,
              showRoadLabels: false,
              zoomControl: false,
              fullscreenControl: false,
              motionTrackingControl: false,
              motionTracking: false,
            }}
          />
        </Box>
      </Box>

      <Box sx={{ display: 'flex', gap: 2, justifyContent: 'center' }}>
        <Button
          variant="contained"
          color="primary"
          onClick={onPreviousLandmark}
          disabled={currentIndex === 0}
        >
          Previous Landmark
        </Button>
        <Button
          variant="contained"
          color={currentLandmark.isArrived ? "secondary" : "primary"}
          onClick={onMarkArrived}
        >
          {currentLandmark.isArrived ? "Mark as Not Arrived" : "Mark as Arrived"}
        </Button>
        <Button
          variant="contained"
          color="primary"
          onClick={onNextLandmark}
          disabled={currentIndex === landmarks.length - 1 && !isComplete}
        >
          Next Landmark
        </Button>
      </Box>

      {isComplete && (
        <Box sx={{ mt: 3, textAlign: 'center' }}>
          <Typography variant="h5" gutterBottom>
            Congratulations! You've completed all landmarks!
          </Typography>
          <Button
            variant="contained"
            color="primary"
            onClick={onShowOverview}
          >
            Return to Overview
          </Button>
        </Box>
      )}
    </Box>
  );
};

export default NavigationView;
