import React, { useEffect, useState } from 'react';
import {
  Box,
  Paper,
  Typography,
  Button,
  Stepper,
  Step,
  StepLabel,
  Alert,
} from '@mui/material';
import { GoogleMap, useLoadScript, Marker } from '@react-google-maps/api';
import { Landmark } from '../types';

const libraries: ("places" | "drawing" | "geometry" | "visualization")[] = ["places"];

interface NavigationViewProps {
  landmarks: Landmark[];
  currentIndex: number;
  isComplete: boolean;
  onMarkArrived: () => void;
  onNextLandmark: () => void;
  onShowOverview: () => void;
}

const NavigationView: React.FC<NavigationViewProps> = ({
  landmarks,
  currentIndex,
  isComplete,
  onMarkArrived,
  onNextLandmark,
  onShowOverview,
}) => {
  const { isLoaded, loadError } = useLoadScript({
    googleMapsApiKey: process.env.REACT_APP_GOOGLE_MAPS_API_KEY || '',
    libraries,
  });

  const [streetViewAvailable, setStreetViewAvailable] = useState(false);
  const [streetViewError, setStreetViewError] = useState<string | null>(null);
  const [nearestStreetView, setNearestStreetView] = useState<google.maps.StreetViewPanorama | null>(null);
  const currentLandmark = landmarks[currentIndex];

  useEffect(() => {
    if (isLoaded && currentLandmark) {
      const streetViewService = new google.maps.StreetViewService();

      // First try to get Street View at the exact location
      streetViewService.getPanorama(
        { location: currentLandmark.position, radius: 50 },
        (data, status) => {
          if (status === 'OK' && data) {
            setStreetViewAvailable(true);
            setStreetViewError(null);
            const panorama = new google.maps.StreetViewPanorama(
              document.getElementById('street-view') as HTMLElement,
              {
                position: currentLandmark.position,
                pov: {
                  heading: 34,
                  pitch: 10,
                },
                addressControl: false,
                showRoadLabels: false,
                zoomControl: false,
              }
            );
            setNearestStreetView(panorama);
          } else {
            // If no Street View at exact location, try to find the nearest available point
            streetViewService.getPanorama(
              { location: currentLandmark.position, radius: 1000 },
              (nearestData, nearestStatus) => {
                if (nearestStatus === 'OK' && nearestData?.location?.latLng) {
                  setStreetViewAvailable(true);
                  setStreetViewError('Showing the nearest available Street View location.');
                  const panorama = new google.maps.StreetViewPanorama(
                    document.getElementById('street-view') as HTMLElement,
                    {
                      position: nearestData.location.latLng,
                      pov: {
                        heading: google.maps.geometry.spherical.computeHeading(
                          nearestData.location.latLng,
                          currentLandmark.position
                        ),
                        pitch: 10,
                      },
                      addressControl: false,
                      showRoadLabels: false,
                      zoomControl: false,
                    }
                  );
                  setNearestStreetView(panorama);
                } else {
                  setStreetViewAvailable(false);
                  setStreetViewError('No Street View available nearby. Showing map view instead.');
                }
              }
            );
          }
        }
      );
    }
  }, [isLoaded, currentLandmark]);

  if (!currentLandmark) {
    return (
      <Box sx={{ textAlign: 'center', py: 4 }}>
        <Typography variant="h4" gutterBottom>
          Navigation Complete!
        </Typography>
        <Button variant="contained" color="primary" onClick={onShowOverview}>
          Back to Overview
        </Button>
      </Box>
    );
  }

  if (loadError) {
    return <Typography color="error">Error loading Google Maps</Typography>;
  }

  if (!isLoaded) {
    return <Typography>Loading...</Typography>;
  }

  return (
    <Box>
      <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 3 }}>
        <Typography variant="h4">Navigation</Typography>
        <Button variant="outlined" onClick={onShowOverview}>
          Show Overview
        </Button>
      </Box>

      <Paper sx={{ p: 3, mb: 3 }}>
        <Stepper activeStep={currentIndex} alternativeLabel sx={{ mb: 3 }}>
          {landmarks.map((landmark, index) => (
            <Step key={landmark.id}>
              <StepLabel>
                {landmark.name}
                {landmark.isArrived && ' âœ“'}
              </StepLabel>
            </Step>
          ))}
        </Stepper>

        <Typography variant="h5" gutterBottom>
          Current Landmark: {currentLandmark.name}
        </Typography>
        <Typography variant="body1" gutterBottom>
          Address: {currentLandmark.address}
        </Typography>

        {streetViewError && (
          <Alert severity="info" sx={{ mb: 2 }}>
            {streetViewError}
          </Alert>
        )}

        <Box sx={{ height: 400, width: '100%', mb: 3, display: 'flex', gap: 2 }}>
          <Box sx={{ flex: 1 }}>
            <GoogleMap
              mapContainerStyle={{ width: '100%', height: '100%' }}
              center={currentLandmark.position}
              zoom={15}
            >
              {landmarks.map((landmark, index) => (
                <Marker
                  key={landmark.id}
                  position={landmark.position}
                  icon={{
                    path: google.maps.SymbolPath.CIRCLE,
                    scale: index === currentIndex ? 10 : 7,
                    fillColor: index === currentIndex ? '#1976d2' : '#dc004e',
                    fillOpacity: 1,
                    strokeColor: '#ffffff',
                    strokeWeight: 2,
                  }}
                />
              ))}
            </GoogleMap>
          </Box>
          <Box sx={{ flex: 1 }}>
            {streetViewAvailable ? (
              <div
                id="street-view"
                style={{ width: '100%', height: '100%' }}
              />
            ) : (
              <Box
                sx={{
                  width: '100%',
                  height: '100%',
                  display: 'flex',
                  flexDirection: 'column',
                  justifyContent: 'center',
                  alignItems: 'center',
                  bgcolor: 'grey.100',
                  p: 2,
                }}
              >
                <Typography variant="h6" gutterBottom>
                  Map View
                </Typography>
                <Typography variant="body2" color="text.secondary" align="center">
                  Street View is not available at this location. Use the map on the left to navigate.
                </Typography>
              </Box>
            )}
          </Box>
        </Box>

        <Box sx={{ display: 'flex', gap: 2, justifyContent: 'center' }}>
          <Button
            variant="contained"
            color="primary"
            onClick={onMarkArrived}
            disabled={currentLandmark.isArrived}
          >
            Mark as Arrived
          </Button>
          <Button
            variant="contained"
            color="secondary"
            onClick={onNextLandmark}
            disabled={!currentLandmark.isArrived || currentIndex === landmarks.length - 1}
          >
            Next Landmark
          </Button>
        </Box>
      </Paper>
    </Box>
  );
};

export default NavigationView;
